package com.superman.animate.customview.day1import androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport com.superman.animate.Rimport kotlinx.android.synthetic.main.activity_custom_view_bitmap_shaper.*class CustomViewBitmapShaperActivity : AppCompatActivity() {    private val data = mutableListOf(        PiePercentsView.PercentData(40f, "RED", "#FF0000"),        PiePercentsView.PercentData(30f, "GREEN", "#00FF00"),        PiePercentsView.PercentData(20f, "BLUE", "#0000FF"),        PiePercentsView.PercentData(15f, "YELLOW", "#EDED54"),        PiePercentsView.PercentData(10f, "PINK", "#E6D6FE"),        PiePercentsView.PercentData(10f, "GREY", "#D8D8D8"),    )    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_custom_view_bitmap_shaper)        piePercentsView.setData(data)    }}