package com.superman.animate.navigationimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.view.MenuItemimport androidx.navigation.NavControllerimport androidx.navigation.Navigationimport androidx.navigation.fragment.NavHostFragmentimport androidx.navigation.ui.setupWithNavControllerimport com.jaeger.library.StatusBarUtilimport com.superman.animate.Rimport kotlinx.android.synthetic.main.activity_navigation_test.*class NavigationTestActivity : AppCompatActivity() {    private lateinit var navController: NavController    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_navigation_test)        initView()    }    private fun initView() {        StatusBarUtil.setLightMode(this)        setSupportActionBar(toolBar)        supportActionBar?.setDisplayHomeAsUpEnabled(true) //返回按键        supportActionBar?.setDisplayShowTitleEnabled(true)        navController = Navigation.findNavController(this, R.id.nav_host_fragment)        toolBar.setNavigationOnClickListener {            navController.navigateUp()        }        initBottomNavigation()    }    private fun initBottomNavigation() {        bottom_tabbar?.setupWithNavController(navController)        navController.addOnDestinationChangedListener { _, destination, _ ->            toolBar.title = destination.label        }    }    override fun onOptionsItemSelected(item: MenuItem): Boolean {        if (item.itemId==android.R.id.home) {            finish()        }        return super.onOptionsItemSelected(item)    }}