package com.superman.maidlimport android.os.Parcelimport android.os.Parcelabledata class BookBean(    var name: String?,    var author: String?,    var publishTime: String?,) : Parcelable {    constructor(parcel: Parcel) : this(        parcel.readString(),        parcel.readString(),        parcel.readString()    )    override fun writeToParcel(parcel: Parcel, flags: Int) {        parcel.writeString(name)        parcel.writeString(author)        parcel.writeString(publishTime)    }    override fun describeContents(): Int {        return 0    }    companion object CREATOR : Parcelable.Creator<BookBean> {        override fun createFromParcel(parcel: Parcel): BookBean {            return BookBean(parcel)        }        override fun newArray(size: Int): Array<BookBean?> {            return arrayOfNulls(size)        }    }}